mapper.select:
SELECT g.id, g.student_id, g.course_id, g.score, g.created_at, c.name as course_name, c.semester, c.credit, c.teacher_id, c.classroom_id, cr.name as classroom_name, t.name as teacher_name FROM grade g JOIN course c ON g.course_id = c.id JOIN teacher t ON c.teacher_id = t.id LEFT JOIN classroom cr ON c.classroom_id = cr.id WHERE g.student_id = #{studentId}
AND c.semester = #{semester}
ORDER BY g.created_at DESC

mapper.update:
UPDATE grade 
SET score = #{score}
WHERE student_id = #{studentId} AND course_id = #{courseId}

update.foreach:
UPDATE grade 
SET score = #{grade.score}
WHERE student_id = #{grade.studentId} 
AND course_id = #{grade.courseId}
