mapper.select:
SELECT * FROM teacher
AND name LIKE CONCAT('%', #{search}, '%')
ORDER BY id ASC LIMIT #{offset}, #{size}

mapper.update:
UPDATE teacher
SET name = #{name},
    user_id = #{userId}
WHERE id = #{id}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM course 
    WHERE teacher_id = #{id} 
    AND semester = (
        SELECT value FROM system_config WHERE key = 'current_semester'
    )
)
